    @Test
    public void testRecordMetricsUnauthenticated() {
        ZMSImpl.metric = Mockito.mock(Metric.class);
        RsrcCtxWrapper ctx = (RsrcCtxWrapper) zmsTestInitializer.getZms().newResourceContext(zmsTestInitializer.getMockServletRequest(), zmsTestInitializer.getMockServletResponse(), "someApiMethod");
        String testDomain = "testDomain";
        int httpStatus = 200;
        ctx.setRequestDomain(testDomain);
        zmsTestInitializer.getZms().recordMetrics(ctx, httpStatus);
        verify(ZMSImpl.metric,
                times(1)).increment (
                eq("zms_api"),
                eq(testDomain),
                eq(null),
                eq("GET"),
                eq(httpStatus),
                eq("someapimethod"));
        verify(ZMSImpl.metric,
                times(1)).stopTiming (
                eq(ctx.getTimerMetric()),
                eq(testDomain),
                eq(null),
                eq("GET"),
                eq(httpStatus),
                eq("someapimethod_timing"));
        verify(ZMSImpl.metric,
                times(1)).startTiming (
                eq("zms_api_latency"),
                eq(null),
                eq(null),
                eq("GET"),
                eq("someapimethod"));
    }

