            dom1.setAzureSubscription("invalid-azure-subscription");
            zmsTestInitializer.getZms().postTopLevelDomain(zmsTestInitializer.getMockDomRsrcCtx(), zmsTestInitializer.getAuditRef(), dom1);
            fail();
